"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import { useMutation } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { ArrowLeft } from "lucide-react";
import Link from "next/link";
import { toast } from "sonner";
import { formatNumberWithCommas, removeCommas } from "@/lib/utils/format";

type AcquisitionType = "stock" | "land" | "company" | "livestock" | "ip" | "other";

const typeLabels = {
  stock: "Stock",
  land: "Land",
  company: "Company",
  livestock: "Livestock",
  ip: "Intellectual Property",
  other: "Other",
};

export default function NewAcquisitionPage() {
  const router = useRouter();
  const propose = useMutation(api.acquisitions.propose);

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [type, setType] = useState<AcquisitionType>("stock");
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [estimatedCost, setEstimatedCost] = useState("");
  const [unitsNeeded, setUnitsNeeded] = useState("");

  // Type-specific fields
  const [ticker, setTicker] = useState("");
  const [location, setLocation] = useState("");
  const [acreage, setAcreage] = useState("");
  const [quantity, setQuantity] = useState("");
  const [url, setUrl] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const metadata: {
        ticker?: string;
        location?: string;
        acreage?: number;
        quantity?: number;
        url?: string;
      } = {};

      if (ticker) metadata.ticker = ticker.toUpperCase();
      if (location) metadata.location = location;
      if (acreage) metadata.acreage = parseFloat(acreage);
      if (quantity) metadata.quantity = parseInt(quantity);
      if (url) metadata.url = url;

      await propose({
        title,
        description,
        type,
        metadata,
        estimatedCost: estimatedCost ? Math.round(parseFloat(estimatedCost) * 100) : undefined,
        unitsNeeded: unitsNeeded ? parseInt(unitsNeeded) : undefined,
      });

      toast.success("Acquisition proposed successfully!");
      router.push("/acquisitions");
    } catch (error) {
      toast.error("Failed to propose acquisition");
      console.error(error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="space-y-6">
      {/* Back button */}
      <Link href="/acquisitions">
        <Button variant="ghost" className="gap-2">
          <ArrowLeft className="h-4 w-4" />
          Back to acquisitions
        </Button>
      </Link>

      <div>
        <h1 className="text-2xl font-bold text-foreground">Propose Acquisition</h1>
        <p className="text-muted-foreground">
          Propose a new target for the community to acquire
        </p>
      </div>

      <form onSubmit={handleSubmit}>
        <Card className="border-border bg-card">
          <CardHeader>
            <CardTitle className="text-foreground">Acquisition Details</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Type */}
            <div className="space-y-2">
              <Label htmlFor="type">Type</Label>
              <Select
                value={type}
                onValueChange={(v) => setType(v as AcquisitionType)}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select type" />
                </SelectTrigger>
                <SelectContent>
                  {Object.entries(typeLabels).map(([value, label]) => (
                    <SelectItem key={value} value={value}>
                      {label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Title */}
            <div className="space-y-2">
              <Label htmlFor="title">Title</Label>
              <Input
                id="title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="e.g., Microsoft Corporation Stock"
                required
              />
            </div>

            {/* Description */}
            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Why should the community acquire this? What value does it bring?"
                rows={4}
                required
              />
            </div>

            {/* Type-specific fields */}
            {type === "stock" && (
              <div className="space-y-2">
                <Label htmlFor="ticker">Stock Ticker</Label>
                <Input
                  id="ticker"
                  value={ticker}
                  onChange={(e) => setTicker(e.target.value)}
                  placeholder="e.g., MSFT"
                  className="uppercase"
                />
              </div>
            )}

            {type === "land" && (
              <>
                <div className="space-y-2">
                  <Label htmlFor="location">Location</Label>
                  <Input
                    id="location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="e.g., Alaska, USA"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="acreage">Acreage</Label>
                  <Input
                    id="acreage"
                    type="text"
                    inputMode="decimal"
                    value={formatNumberWithCommas(acreage)}
                    onChange={(e) => setAcreage(removeCommas(e.target.value))}
                    placeholder="e.g., 100"
                  />
                </div>
              </>
            )}

            {type === "company" && (
              <>
                <div className="space-y-2">
                  <Label htmlFor="location">Headquarters Location</Label>
                  <Input
                    id="location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="e.g., San Francisco, CA"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="url">Website URL</Label>
                  <Input
                    id="url"
                    type="url"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="https://example.com"
                  />
                </div>
              </>
            )}

            {type === "livestock" && (
              <div className="space-y-2">
                <Label htmlFor="quantity">Quantity</Label>
                <Input
                  id="quantity"
                  type="text"
                  inputMode="numeric"
                  value={formatNumberWithCommas(quantity)}
                  onChange={(e) => setQuantity(removeCommas(e.target.value))}
                  placeholder="e.g., 100"
                />
              </div>
            )}

            {type === "ip" && (
              <div className="space-y-2">
                <Label htmlFor="url">Reference URL</Label>
                <Input
                  id="url"
                  type="url"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  placeholder="https://example.com"
                />
              </div>
            )}

            {/* Common optional fields */}
            <div className="grid gap-4 sm:grid-cols-2">
              <div className="space-y-2">
                <Label htmlFor="estimatedCost">Estimated Cost ($)</Label>
                <Input
                  id="estimatedCost"
                  type="number"
                  step="0.01"
                  value={estimatedCost}
                  onChange={(e) => setEstimatedCost(e.target.value)}
                  placeholder="e.g., 1000000"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="unitsNeeded">Units Needed</Label>
                <Input
                  id="unitsNeeded"
                  type="number"
                  value={unitsNeeded}
                  onChange={(e) => setUnitsNeeded(e.target.value)}
                  placeholder="e.g., 100000"
                />
              </div>
            </div>

            {/* External URL for all types */}
            {type !== "company" && type !== "ip" && (
              <div className="space-y-2">
                <Label htmlFor="url">External Link (optional)</Label>
                <Input
                  id="url"
                  type="url"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  placeholder="https://example.com"
                />
              </div>
            )}

            {/* Submit */}
            <div className="flex justify-end gap-3">
              <Link href="/acquisitions">
                <Button variant="outline" type="button">
                  Cancel
                </Button>
              </Link>
              <Button type="submit" disabled={isSubmitting}>
                {isSubmitting ? "Proposing..." : "Propose Acquisition"}
              </Button>
            </div>
          </CardContent>
        </Card>
      </form>
    </div>
  );
}

