import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus, TrendingUp, TrendingDown, Building, Landmark, Tractor, Lightbulb, MoreHorizontal } from "lucide-react";
import Link from "next/link";
import { useState } from "react";

const typeIcons = {
  stock: TrendingUp,
  land: Landmark,
  company: Building,
  livestock: Tractor,
  ip: Lightbulb,
  other: MoreHorizontal,
};

const typeLabels = {
  stock: "Stock",
  land: "Land",
  company: "Company",
  livestock: "Livestock",
  ip: "Intellectual Property",
  other: "Other",
};

const statusColors = {
  proposed: "bg-yellow-500/10 text-yellow-500 border-yellow-500/20",
  approved: "bg-green-500/10 text-green-500 border-green-500/20",
  in_progress: "bg-blue-500/10 text-blue-500 border-blue-500/20",
  acquired: "bg-purple-500/10 text-purple-500 border-purple-500/20",
  rejected: "bg-red-500/10 text-red-500 border-red-500/20",
};

const statusProgress: Record<string, { percent: number; color: string }> = {
  proposed: { percent: 20, color: "bg-yellow-500" },
  approved: { percent: 50, color: "bg-green-500" },
  in_progress: { percent: 75, color: "bg-blue-500" },
  acquired: { percent: 100, color: "bg-purple-500" },
  rejected: { percent: 0, color: "bg-red-500" },
};

export default function AcquisitionsPage() {
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [typeFilter, setTypeFilter] = useState<string>("all");

  const user = useQuery(api.users.me);
  const acquisitions = useQuery(
    api.acquisitions.list,
    user
      ? {
          status:
            statusFilter !== "all"
              ? (statusFilter as "proposed" | "approved" | "in_progress" | "acquired" | "rejected")
              : undefined,
        }
      : "skip"
  );

  const filteredAcquisitions = acquisitions?.filter((a) =>
    typeFilter === "all" ? true : a.type === typeFilter
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-2xl font-bold text-foreground">Target Acquisitions</h1>
          <p className="text-muted-foreground">
            Assets we&apos;re targeting to acquire from Babylon
          </p>
        </div>
        <Link href="/acquisitions/new">
          <Button className="gap-2">
            <Plus className="h-4 w-4" />
            Propose Acquisition
          </Button>
        </Link>
      </div>

      {/* Filters */}
      <div className="flex flex-wrap gap-3">
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-[150px]">
            <SelectValue placeholder="Status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="proposed">Proposed</SelectItem>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="in_progress">In Progress</SelectItem>
            <SelectItem value="acquired">Acquired</SelectItem>
            <SelectItem value="rejected">Rejected</SelectItem>
          </SelectContent>
        </Select>

        <Select value={typeFilter} onValueChange={setTypeFilter}>
          <SelectTrigger className="w-[150px]">
            <SelectValue placeholder="Type" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Types</SelectItem>
            <SelectItem value="stock">Stock</SelectItem>
            <SelectItem value="land">Land</SelectItem>
            <SelectItem value="company">Company</SelectItem>
            <SelectItem value="livestock">Livestock</SelectItem>
            <SelectItem value="ip">IP</SelectItem>
            <SelectItem value="other">Other</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Acquisitions Grid */}
      {filteredAcquisitions === undefined ? (
        <div className="text-center py-12 text-muted-foreground">Loading...</div>
      ) : filteredAcquisitions.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-muted-foreground">No acquisitions found.</p>
            <Link href="/acquisitions/new">
              <Button className="mt-4">Propose the first one</Button>
            </Link>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-3">
          {filteredAcquisitions.map((acquisition) => {
            const TypeIcon = typeIcons[acquisition.type];
            return (
              <Link
                key={acquisition._id}
                href={`/acquisitions/${acquisition._id}`}
              >
                <Card className="transition-colors hover:border-primary/50">
                  <CardContent className="flex items-center gap-4 p-4">
                    <div className="flex h-10 w-10 flex-shrink-0 items-center justify-center rounded-lg bg-muted">
                      <TypeIcon className="h-5 w-5 text-muted-foreground" />
                    </div>

                    <div className="min-w-0 flex-1">
                      <div className="flex items-center gap-2">
                        <h3 className="truncate font-semibold text-foreground">
                          {acquisition.title}
                        </h3>
                        <Badge
                          variant="outline"
                          className={`flex-shrink-0 ${statusColors[acquisition.status]}`}
                        >
                          {acquisition.status}
                        </Badge>
                      </div>
                      <p className="mt-0.5 truncate text-sm text-muted-foreground">
                        {acquisition.description}
                      </p>
                    </div>

                    <div className="hidden flex-shrink-0 text-right sm:block">
                      <span className="text-sm text-muted-foreground/70">
                        {typeLabels[acquisition.type]}
                      </span>
                      {acquisition.estimatedCost && (
                        <p className="font-medium text-foreground">
                          ${(acquisition.estimatedCost / 100).toLocaleString()}
                        </p>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </Link>
            );
          })}
        </div>
      )}
    </div>
  );
}

