                  <div className="h-1 w-full bg-muted">
                    <div
                      className={`h-full transition-all ${progress.color}`}
                      style={{ width: `${progress.percent}%` }}
                    />
                  </div>
                </Card>
              </Link>
            );
          })}
        </div>
      )}
    </div>
  );
}

