   341→                  </div>
   342→                </div>
   343→              </div>
   344→            </CardContent>
   345→          </Card>
   346→
   347→          {/* Vote Score */}
   348→          <Card className="border-border bg-card">
   349→            <CardContent className="pt-6">
   350→              <div className="text-center">
   351→                <div className="text-3xl font-bold text-foreground">
   352→                  {acquisition.voteScore > 0 ? "+" : ""}
   353→                  {acquisition.voteScore}
   354→                </div>
   355→                <div className="text-sm text-muted-foreground">Vote Score</div>
   356→              </div>
   357→            </CardContent>
   358→          </Card>
   359→        </div>
   360→      </div>
   361→    </div>
   362→  );
   363→}
   364→

