toolUseResult.content:
"use client";

import { useParams, useRouter } from "next/navigation";
import { useQuery, useMutation } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Id } from "@/convex/_generated/dataModel";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  ArrowLeft,
  ThumbsUp,
  ThumbsDown,
  ExternalLink,
  MapPin,
  Hash,
  DollarSign,
  Calendar,
  User,
  Trash2,
  Pencil,
  TrendingUp,
  Building,
  Landmark,
  Tractor,
  Lightbulb,
  MoreHorizontal,
} from "lucide-react";
import Link from "next/link";
import { toast } from "sonner";

const statusConfig = {
  proposed: {
    label: "Proposed",
    badge: "bg-amber-50 text-amber-700 border-amber-200",
    progress: 20,
    progressColor: "bg-amber-400",
  },
  approved: {
    label: "Approved",
    badge: "bg-emerald-50 text-emerald-700 border-emerald-200",
    progress: 50,
    progressColor: "bg-emerald-400",
  },
  in_progress: {
    label: "In Progress",
    badge: "bg-blue-50 text-blue-700 border-blue-200",
    progress: 75,
    progressColor: "bg-blue-400",
  },
  acquired: {
    label: "Acquired",
    badge: "bg-violet-50 text-violet-700 border-violet-200",
    progress: 100,
    progressColor: "bg-violet-400",
  },
  rejected: {
    label: "Rejected",
    badge: "bg-red-50 text-red-700 border-red-200",
    progress: 0,
    progressColor: "bg-red-400",
  },
};

const typeConfig = {
  stock: { label: "Stock", icon: TrendingUp },
  land: { label: "Land", icon: Landmark },
  company: { label: "Company", icon: Building },
  livestock: { label: "Livestock", icon: Tractor },
  ip: { label: "Intellectual Property", icon: Lightbulb },
  other: { label: "Other", icon: MoreHorizontal },
};

function DetailRow({
  icon: Icon,
  label,
  children,
}: {
  icon: React.ElementType;
  label: string;
  children: React.ReactNode;
}) {
  return (
    <div className="flex items-start gap-3">
      <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-md bg-muted">
        <Icon className="h-4 w-4 text-muted-foreground" />
      </div>
      <div className="min-w-0 flex-1 pt-0.5">
        <div className="text-xs text-muted-foreground">{label}</div>
        <div className="mt-0.5 text-sm font-medium text-foreground">{children}</div>
      </div>
    </div>
  );
}

export default function AcquisitionDetailPage() {
  const params = useParams();
  const router = useRouter();
  const id = params.id as Id<"acquisitionTargets">;

  const acquisition = useQuery(api.acquisitions.get, { id });
  const currentUser = useQuery(api.users.me);
  const myVote = useQuery(api.votes.getMyVote, { acquisitionId: id });
  const castVote = useMutation(api.votes.castVote);
  const removeVote = useMutation(api.votes.removeVote);
  const updateStatus = useMutation(api.acquisitions.updateStatus);
  const deleteAcquisition = useMutation(api.acquisitions.remove);

  if (acquisition === undefined) {
    return (
      <div className="flex items-center justify-center py-24">
        <div className="text-sm text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (acquisition === null) {
    return (
      <div className="flex flex-col items-center justify-center py-24">
        <p className="text-sm text-muted-foreground">Acquisition not found</p>
        <Link href="/acquisitions">
          <Button variant="outline" size="sm" className="mt-4">
            Back to acquisitions
          </Button>
        </Link>
      </div>
    );
  }

  const handleVote = async (vote: "up" | "down") => {
    try {
      if (myVote?.vote === vote) {
        await removeVote({ acquisitionId: id });
        toast.success("Vote removed");
      } else {
        await castVote({ acquisitionId: id, vote });
        toast.success(vote === "up" ? "Upvoted!" : "Downvoted");
      }
    } catch {
      toast.error("Failed to vote");
    }
  };

  const handleStatusChange = async (
    status: "proposed" | "approved" | "in_progress" | "acquired" | "rejected"
  ) => {
    try {
      await updateStatus({ id, status });
      toast.success(`Status updated to ${status}`);
    } catch {
      toast.error("Failed to update status");
    }
  };

  const handleDelete = async () => {
    if (!confirm("Are you sure you want to delete this acquisition?")) return;
    try {
      await deleteAcquisition({ id });
      toast.success("Acquisition deleted");
      router.push("/acquisitions");
    } catch {
      toast.error("Failed to delete acquisition");
    }
  };

  const isAdmin = currentUser?.role === "admin";
  const isProposer = currentUser?._id === acquisition.proposedBy;
  const canEdit = isAdmin || (isProposer && acquisition.status === "proposed");
  const canDelete = isAdmin || (isProposer && acquisition.status === "proposed");

  const status = statusConfig[acquisition.status];
  const typeInfo = typeConfig[acquisition.type];
  const TypeIcon = typeInfo.icon;

  return (
    <div className="space-y-8">
      {/* Header */}
      <div>
        <Link
          href="/acquisitions"
          className="inline-flex items-center gap-1.5 text-sm text-muted-foreground transition-colors hover:text-foreground"
        >
          <ArrowLeft className="h-4 w-4" />
          Back to acquisitions
        </Link>

        <div className="mt-6 flex flex-col gap-4 sm:flex-row sm:items-start sm:justify-between">
          <div className="flex items-start gap-4">
            <div className="flex h-12 w-12 shrink-0 items-center justify-center rounded-xl bg-muted">
              <TypeIcon className="h-6 w-6 text-muted-foreground" />
            </div>
            <div>
              <div className="flex items-center gap-3">
                <h1 className="text-2xl font-semibold tracking-tight text-foreground">
                  {acquisition.title}
                </h1>
                <Badge variant="outline" className={status.badge}>
                  {status.label}
                </Badge>
              </div>
              <p className="mt-1 text-sm text-muted-foreground">
                {typeInfo.label}
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {canEdit && (
              <Link href={`/acquisitions/${id}/edit`}>
                <Button variant="outline" size="sm" className="gap-1.5">
                  <Pencil className="h-3.5 w-3.5" />
                  Edit
                </Button>
              </Link>
            )}
            {canDelete && (
              <Button
                variant="outline"
                size="sm"
                className="gap-1.5 text-red-600 hover:bg-red-50 hover:text-red-700"
                onClick={handleDelete}
              >
                <Trash2 className="h-3.5 w-3.5" />
                Delete
              </Button>
            )}
          </div>
        </div>
      </div>

      {/* Progress bar */}
      <div className="space-y-2">
        <div className="flex items-center justify-between text-sm">
          <span className="text-muted-foreground">Progress</span>
          <span className="font-medium tabular-nums text-foreground">
            {status.progress}%
          </span>
        </div>
        <div className="h-2 overflow-hidden rounded-full bg-muted">
          <div
            className={`h-full rounded-full transition-all ${status.progressColor}`}
            style={{ width: `${status.progress}%` }}
          />
        </div>
      </div>

      <div className="grid gap-8 lg:grid-cols-3">
        {/* Main content */}
        <div className="space-y-8 lg:col-span-2">
          {/* Description */}
          <section>
            <h2 className="text-sm font-medium text-foreground">Description</h2>
            <div className="mt-3 rounded-lg border border-border bg-card p-4">
              <p className="whitespace-pre-wrap text-sm leading-relaxed text-foreground/80">
                {acquisition.description}
              </p>
            </div>
          </section>

          {/* Admin Controls */}
          {isAdmin && (
            <section>
              <h2 className="text-sm font-medium text-foreground">Admin Controls</h2>
              <p className="mt-1 text-xs text-muted-foreground">
                Change the status of this acquisition
              </p>
              <div className="mt-3 rounded-lg border border-border bg-card p-4">
                <div className="flex flex-wrap gap-2">
                  {(
                    ["proposed", "approved", "in_progress", "acquired", "rejected"] as const
                  ).map((s) => {
                    const config = statusConfig[s];
                    const isActive = acquisition.status === s;
                    return (
                      <Button
                        key={s}
                        variant={isActive ? "default" : "outline"}
                        size="sm"
                        className={isActive ? "" : "text-muted-foreground"}
                        onClick={() => handleStatusChange(s)}
                      >
                        {config.label}
                      </Button>
                    );
                  })}
                </div>
              </div>
            </section>
          )}
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Voting Card */}
          <div className="rounded-lg border border-border bg-card p-4">
            <div className="text-center">
              <div
                className={`text-3xl font-bold tabular-nums ${
                  acquisition.voteScore > 0
                    ? "text-emerald-600"
                    : acquisition.voteScore < 0
                      ? "text-red-600"
                      : "text-foreground"
                }`}
              >
                {acquisition.voteScore > 0 ? "+" : ""}
                {acquisition.voteScore}
              </div>
              <div className="mt-1 text-xs text-muted-foreground">Vote Score</div>
            </div>

            <div className="mt-4 flex gap-2">
              <Button
                variant="outline"
                size="sm"
                className={`flex-1 gap-1.5 ${
                  myVote?.vote === "up"
                    ? "border-emerald-200 bg-emerald-50 text-emerald-700 hover:bg-emerald-100"
                    : ""
                }`}
                onClick={() => handleVote("up")}
              >
                <ThumbsUp className="h-4 w-4" />
                {acquisition.upvotes}
              </Button>
              <Button
                variant="outline"
                size="sm"
                className={`flex-1 gap-1.5 ${
                  myVote?.vote === "down"
                    ? "border-red-200 bg-red-50 text-red-700 hover:bg-red-100"
                    : ""
                }`}
                onClick={() => handleVote("down")}
              >
                <ThumbsDown className="h-4 w-4" />
                {acquisition.downvotes}
              </Button>
            </div>
          </div>

          {/* Details Card */}
          <div className="rounded-lg border border-border bg-card">
            <div className="border-b border-border px-4 py-3">
              <h3 className="text-sm font-medium text-foreground">Details</h3>
            </div>
            <div className="space-y-4 p-4">
              {acquisition.estimatedCost && (
                <DetailRow icon={DollarSign} label="Estimated Cost">
                  ${(acquisition.estimatedCost / 100).toLocaleString()}
                </DetailRow>
              )}

              {acquisition.unitsNeeded && (
                <DetailRow icon={Hash} label="Units Needed">
                  {acquisition.unitsNeeded.toLocaleString()}
                </DetailRow>
              )}

              {acquisition.metadata.ticker && (
                <DetailRow icon={TrendingUp} label="Ticker">
                  {acquisition.metadata.ticker}
                </DetailRow>
              )}

              {acquisition.metadata.location && (
                <DetailRow icon={MapPin} label="Location">
                  {acquisition.metadata.location}
                </DetailRow>
              )}

              {acquisition.metadata.acreage && (
                <DetailRow icon={Landmark} label="Acreage">
                  {acquisition.metadata.acreage.toLocaleString()} acres
                </DetailRow>
              )}

              {acquisition.metadata.quantity && (
                <DetailRow icon={Hash} label="Quantity">
                  {acquisition.metadata.quantity.toLocaleString()}
                </DetailRow>
              )}

              {acquisition.metadata.url && (
                <DetailRow icon={ExternalLink} label="External Link">
                  <a
                    href={acquisition.metadata.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:underline"
                  >
                    View Resource
                  </a>
                </DetailRow>
              )}
            </div>
          </div>

          {/* Proposer Info */}
          <div className="rounded-lg border border-border bg-card">
            <div className="border-b border-border px-4 py-3">
              <h3 className="text-sm font-medium text-foreground">Proposal Info</h3>
            </div>
            <div className="space-y-4 p-4">
              <DetailRow icon={User} label="Proposed by">
                {acquisition.proposerName}
              </DetailRow>
              <DetailRow icon={Calendar} label="Proposed on">
                {new Date(acquisition.proposedAt).toLocaleDateString("en-US", {
                  year: "numeric",
                  month: "long",
                  day: "numeric",
                })}
              </DetailRow>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

toolUseResult.originalFile:
"use client";

import { useParams, useRouter } from "next/navigation";
import { useQuery, useMutation } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Id } from "@/convex/_generated/dataModel";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  ArrowLeft,
  ThumbsUp,
  ThumbsDown,
  ExternalLink,
  MapPin,
  Hash,
  DollarSign,
  Calendar,
  User,
  Trash2,
  Pencil,
} from "lucide-react";
import Link from "next/link";
import { toast } from "sonner";

const statusColors = {
  proposed: "bg-yellow-500/10 text-yellow-500 border-yellow-500/20",
  approved: "bg-green-500/10 text-green-500 border-green-500/20",
  in_progress: "bg-blue-500/10 text-blue-500 border-blue-500/20",
  acquired: "bg-purple-500/10 text-purple-500 border-purple-500/20",
  rejected: "bg-red-500/10 text-red-500 border-red-500/20",
};

const typeLabels = {
  stock: "Stock",
  land: "Land",
  company: "Company",
  livestock: "Livestock",
  ip: "Intellectual Property",
  other: "Other",
};

export default function AcquisitionDetailPage() {
  const params = useParams();
  const router = useRouter();
  const id = params.id as Id<"acquisitionTargets">;

  const acquisition = useQuery(api.acquisitions.get, { id });
  const currentUser = useQuery(api.users.me);
  const myVote = useQuery(api.votes.getMyVote, { acquisitionId: id });
  const castVote = useMutation(api.votes.castVote);
  const removeVote = useMutation(api.votes.removeVote);
  const updateStatus = useMutation(api.acquisitions.updateStatus);
  const deleteAcquisition = useMutation(api.acquisitions.remove);

  if (acquisition === undefined) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-muted-foreground">Loading...</div>
      </div>
    );
  }

  if (acquisition === null) {
    return (
      <div className="flex flex-col items-center justify-center py-12">
        <div className="text-muted-foreground">Acquisition not found</div>
        <Link href="/acquisitions">
          <Button variant="outline" className="mt-4">
            Back to acquisitions
          </Button>
        </Link>
      </div>
    );
  }

  const handleVote = async (vote: "up" | "down") => {
    try {
      if (myVote?.vote === vote) {
        await removeVote({ acquisitionId: id });
        toast.success("Vote removed");
      } else {
        await castVote({ acquisitionId: id, vote });
        toast.success(vote === "up" ? "Upvoted!" : "Downvoted");
      }
    } catch {
      toast.error("Failed to vote");
    }
  };

  const handleStatusChange = async (status: "proposed" | "approved" | "in_progress" | "acquired" | "rejected") => {
    try {
      await updateStatus({ id, status });
      toast.success(`Status updated to ${status}`);
    } catch {
      toast.error("Failed to update status");
    }
  };

  const handleDelete = async () => {
    if (!confirm("Are you sure you want to delete this acquisition?")) return;
    try {
      await deleteAcquisition({ id });
      toast.success("Acquisition deleted");
      router.push("/acquisitions");
    } catch {
      toast.error("Failed to delete acquisition");
    }
  };

  const isAdmin = currentUser?.role === "admin";
  const isProposer = currentUser?._id === acquisition.proposedBy;
  const canEdit = isAdmin || (isProposer && acquisition.status === "proposed");
  const canDelete = isAdmin || (isProposer && acquisition.status === "proposed");

  return (
    <div className="space-y-6">
      {/* Back button */}
      <Link href="/acquisitions">
        <Button variant="ghost" className="gap-2">
          <ArrowLeft className="h-4 w-4" />
          Back to acquisitions
        </Button>
      </Link>

      {/* Header */}
      <div className="flex flex-col gap-4 sm:flex-row sm:items-start sm:justify-between">
        <div>
          <div className="flex items-center gap-3">
            <h1 className="text-2xl font-bold text-foreground">
              {acquisition.title}
            </h1>
            <Badge
              variant="outline"
              className={statusColors[acquisition.status]}
            >
              {acquisition.status}
            </Badge>
          </div>
          <p className="mt-1 text-muted-foreground">
            {typeLabels[acquisition.type]}
          </p>
        </div>

        <div className="flex items-center gap-2">
          {/* Voting */}
          <div className="flex items-center gap-1 rounded-lg border border-border bg-card p-1">
            <Button
              variant="ghost"
              size="sm"
              className={`gap-1 ${myVote?.vote === "up" ? "text-green-500" : ""}`}
              onClick={() => handleVote("up")}
            >
              <ThumbsUp className="h-4 w-4" />
              {acquisition.upvotes}
            </Button>
            <div className="w-px h-6 bg-muted" />
            <Button
              variant="ghost"
              size="sm"
              className={`gap-1 ${myVote?.vote === "down" ? "text-red-500" : ""}`}
              onClick={() => handleVote("down")}
            >
              <ThumbsDown className="h-4 w-4" />
              {acquisition.downvotes}
            </Button>
          </div>

          {canEdit && (
            <Link href={`/acquisitions/${id}/edit`}>
              <Button variant="outline" size="sm">
                <Pencil className="h-4 w-4" />
              </Button>
            </Link>
          )}

          {canDelete && (
            <Button variant="destructive" size="sm" onClick={handleDelete}>
              <Trash2 className="h-4 w-4" />
            </Button>
          )}
        </div>
      </div>

      <div className="grid gap-6 lg:grid-cols-3">
        {/* Main content */}
        <div className="lg:col-span-2 space-y-6">
          <Card className="border-border bg-card">
            <CardHeader>
              <CardTitle className="text-foreground">Description</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="whitespace-pre-wrap text-foreground/80">
                {acquisition.description}
              </p>
            </CardContent>
          </Card>

          {/* Admin status controls */}
          {isAdmin && (
            <Card className="border-border bg-card">
              <CardHeader>
                <CardTitle className="text-foreground">Admin Controls</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex flex-wrap gap-2">
                  {(["proposed", "approved", "in_progress", "acquired", "rejected"] as const).map(
                    (status) => (
                      <Button
                        key={status}
                        variant={acquisition.status === status ? "default" : "outline"}
                        size="sm"
                        onClick={() => handleStatusChange(status)}
                      >
                        {status}
                      </Button>
                    )
                  )}
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          <Card className="border-border bg-card">
            <CardHeader>
              <CardTitle className="text-foreground">Details</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              {acquisition.estimatedCost && (
                <div className="flex items-center gap-3">
                  <DollarSign className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">
                      Estimated Cost
                    </div>
                    <div className="font-medium text-foreground">
                      ${(acquisition.estimatedCost / 100).toLocaleString()}
                    </div>
                  </div>
                </div>
              )}

              {acquisition.unitsNeeded && (
                <div className="flex items-center gap-3">
                  <Hash className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Units Needed</div>
                    <div className="font-medium text-foreground">
                      {acquisition.unitsNeeded.toLocaleString()}
                    </div>
                  </div>
                </div>
              )}

              {acquisition.metadata.ticker && (
                <div className="flex items-center gap-3">
                  <Hash className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Ticker</div>
                    <div className="font-medium text-foreground">
                      {acquisition.metadata.ticker}
                    </div>
                  </div>
                </div>
              )}

              {acquisition.metadata.location && (
                <div className="flex items-center gap-3">
                  <MapPin className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Location</div>
                    <div className="font-medium text-foreground">
                      {acquisition.metadata.location}
                    </div>
                  </div>
                </div>
              )}

              {acquisition.metadata.acreage && (
                <div className="flex items-center gap-3">
                  <MapPin className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Acreage</div>
                    <div className="font-medium text-foreground">
                      {acquisition.metadata.acreage.toLocaleString()} acres
                    </div>
                  </div>
                </div>
              )}

              {acquisition.metadata.quantity && (
                <div className="flex items-center gap-3">
                  <Hash className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Quantity</div>
                    <div className="font-medium text-foreground">
                      {acquisition.metadata.quantity.toLocaleString()}
                    </div>
                  </div>
                </div>
              )}

              {acquisition.metadata.url && (
                <div className="flex items-center gap-3">
                  <ExternalLink className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Link</div>
                    <a
                      href={acquisition.metadata.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-sm text-blue-400 hover:underline"
                    >
                      View External
                    </a>
                  </div>
                </div>
              )}

              <div className="border-t border-border pt-4">
                <div className="flex items-center gap-3">
                  <User className="h-4 w-4 text-muted-foreground/70" />
                  <div>
                    <div className="text-sm text-muted-foreground">Proposed by</div>
                    <div className="font-medium text-foreground">
                      {acquisition.proposerName}
                    </div>
                  </div>
                </div>
              </div>

              <div className="flex items-center gap-3">
                <Calendar className="h-4 w-4 text-muted-foreground/70" />
                <div>
                  <div className="text-sm text-muted-foreground">Proposed</div>
                  <div className="font-medium text-foreground">
                    {new Date(acquisition.proposedAt).toLocaleDateString()}
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Vote Score */}
          <Card className="border-border bg-card">
            <CardContent className="pt-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-foreground">
                  {acquisition.voteScore > 0 ? "+" : ""}
                  {acquisition.voteScore}
                </div>
                <div className="text-sm text-muted-foreground">Vote Score</div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

