     1→import { v } from "convex/values";
     2→import { query, mutation } from "./_generated/server";
     3→import { requireUser, requireAdmin } from "./auth";
     4→
     5→// Acquisition type validator
     6→const acquisitionTypeValidator = v.union(
     7→  v.literal("stock"),
     8→  v.literal("land"),
     9→  v.literal("company"),
    10→  v.literal("livestock"),
    11→  v.literal("ip"),
    12→  v.literal("other")
    13→);
    14→
    15→// Status validator
    16→const statusValidator = v.union(
    17→  v.literal("proposed"),
    18→  v.literal("approved"),
    19→  v.literal("in_progress"),
    20→  v.literal("acquired"),
    21→  v.literal("rejected")
    22→);
    23→
    24→// Metadata validator
    25→const metadataValidator = v.object({
    26→  ticker: v.optional(v.string()),
    27→  acreage: v.optional(v.number()),
    28→  location: v.optional(v.string()),
    29→  quantity: v.optional(v.number()),
    30→  url: v.optional(v.string()),
    31→});
    32→
    33→// List all acquisitions
    34→export const list = query({
    35→  args: {
    36→    status: v.optional(statusValidator),
    37→    type: v.optional(acquisitionTypeValidator),
    38→  },
    39→  handler: async (ctx, args) => {
    40→    await requireUser(ctx);
    41→
    42→    let acquisitions;
    43→
    44→    if (args.status) {
    45→      acquisitions = await ctx.db
    46→        .query("acquisitionTargets")
    47→        .withIndex("by_status", (q) => q.eq("status", args.status!))
    48→        .collect();
    49→    } else {
    50→      acquisitions = await ctx.db.query("acquisitionTargets").collect();
    51→    }
    52→
    53→    // Filter by type if specified (since we can only use one index)
    54→    let filtered = acquisitions;
    55→    if (args.type) {
    56→      filtered = acquisitions.filter((a) => a.type === args.type);
    57→    }
    58→
    59→    // Sort by proposedAt descending
    60→    filtered.sort((a, b) => b.proposedAt - a.proposedAt);
    61→
    62→    // Enrich with proposer info
    63→    const enriched = await Promise.all(
    64→      filtered.map(async (acquisition) => {
    65→        const proposer = await ctx.db.get(acquisition.proposedBy);
    66→        return {
    67→          ...acquisition,
    68→          proposerName: proposer?.name ?? "Unknown",
    69→        };
    70→      })
    71→    );
    72→
    73→    return enriched;
    74→  },
    75→});
    76→
    77→// Get a single acquisition
    78→export const get = query({
    79→  args: { id: v.id("acquisitionTargets") },
    80→  handler: async (ctx, args) => {
    81→    await requireUser(ctx);
    82→
    83→    const acquisition = await ctx.db.get(args.id);
    84→    if (!acquisition) return null;
    85→
    86→    const proposer = await ctx.db.get(acquisition.proposedBy);
    87→
    88→    // Get vote counts
    89→    const votes = await ctx.db
    90→      .query("votes")
    91→      .withIndex("by_acquisitionId", (q) => q.eq("acquisitionId", args.id))
    92→      .collect();
    93→
    94→    const upvotes = votes.filter((v) => v.vote === "up").length;
    95→    const downvotes = votes.filter((v) => v.vote === "down").length;
    96→
    97→    return {
    98→      ...acquisition,
    99→      proposerName: proposer?.name ?? "Unknown",
   100→      upvotes,
   101→      downvotes,
   102→      voteScore: upvotes - downvotes,
   103→    };
   104→  },
   105→});
   106→
   107→// Propose a new acquisition
   108→export const propose = mutation({
   109→  args: {
   110→    title: v.string(),
   111→    description: v.string(),
   112→    type: acquisitionTypeValidator,
   113→    metadata: metadataValidator,
   114→    estimatedCost: v.optional(v.number()),
   115→    unitsNeeded: v.optional(v.number()),
   116→  },
   117→  handler: async (ctx, args) => {
   118→    const user = await requireUser(ctx);
   119→
   120→    const acquisitionId = await ctx.db.insert("acquisitionTargets", {
   121→      title: args.title,
   122→      description: args.description,
   123→      type: args.type,
   124→      metadata: args.metadata,
   125→      estimatedCost: args.estimatedCost,
   126→      unitsNeeded: args.unitsNeeded,
   127→      status: "proposed",
   128→      proposedBy: user._id,
   129→      proposedAt: Date.now(),
   130→    });
   131→
   132→    return acquisitionId;
   133→  },
   134→});
   135→
   136→// Update an acquisition (proposer or admin)
   137→export const update = mutation({
   138→  args: {
   139→    id: v.id("acquisitionTargets"),
   140→    title: v.optional(v.string()),
   141→    description: v.optional(v.string()),
   142→    type: v.optional(acquisitionTypeValidator),
   143→    metadata: v.optional(metadataValidator),
   144→    estimatedCost: v.optional(v.number()),
   145→    unitsNeeded: v.optional(v.number()),
   146→  },
   147→  handler: async (ctx, args) => {
   148→    const user = await requireUser(ctx);
   149→
   150→    const acquisition = await ctx.db.get(args.id);
   151→    if (!acquisition) {
   152→      throw new Error("Acquisition not found");
