"use client";

import Link from "next/link";
import { UserButton } from "@clerk/nextjs";
import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";
import {
  Menu,
  BookOpen,
  Crown,
  ArrowLeftRight,
  Building2,
  Wheat,
  ChevronDown,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  Sheet,
  SheetContent,
  SheetTrigger,
} from "@/components/ui/sheet";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Sidebar } from "./sidebar";
import { ThemeToggle } from "@/components/theme-toggle";

// POLOE Platform Apps
const CURRENT_APP = "VES";

const POLOE_APPS = [
  { name: "BOOK", href: "https://poloe.net", icon: BookOpen },
  { name: "KEYS", href: "https://keys.poloe.net", icon: Crown },
  { name: "VES", href: "https://ves.poloe.net", icon: ArrowLeftRight },
  { name: "DOTBOC", href: "https://dotboc.poloe.net", icon: Building2 },
  { name: "DEGANIA", href: "https://degania.poloe.net", icon: Wheat },
];

export function Header() {
  const user = useQuery(api.users.me);

  // Get current app info
  const currentApp = POLOE_APPS.find((app) => app.name === CURRENT_APP) || POLOE_APPS[2];
  const CurrentIcon = currentApp.icon;

  return (
    <header className="flex h-16 items-center justify-between border-b border-border bg-background px-4 lg:px-6">
      {/* Left side - Mobile menu + App switcher */}
      <div className="flex items-center gap-2">
        {/* Mobile menu */}
        <Sheet>
          <SheetTrigger asChild>
            <Button variant="ghost" size="icon" className="lg:hidden">
              <Menu className="h-5 w-5" />
            </Button>
          </SheetTrigger>
          <SheetContent side="left" className="w-64 p-0">
            <Sidebar />
          </SheetContent>
        </Sheet>

        {/* App Switcher */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" className="gap-2">
              <CurrentIcon className="h-4 w-4" />
              <span className="font-medium">{currentApp.name}</span>
              <ChevronDown className="h-4 w-4 text-muted-foreground" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="start" className="w-48">
            {POLOE_APPS.map((app) => {
              const Icon = app.icon;
              const isCurrent = app.name === CURRENT_APP;
              return (
                <DropdownMenuItem key={app.name} asChild>
                  <Link
                    href={app.href}
                    className={`flex items-center gap-3 ${isCurrent ? "bg-muted" : ""}`}
                  >
                    <Icon className="h-4 w-4" />
                    <span>{app.name}</span>
                  </Link>
                </DropdownMenuItem>
              );
            })}
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Right side */}
      <div className="flex items-center gap-2">
        <ThemeToggle />
        {user && (
          <span className="hidden text-sm text-muted-foreground sm:block">
            {user.name}
          </span>
        )}
        <UserButton
          appearance={{
            elements: {
              avatarBox: "h-8 w-8",
            },
          }}
        />
      </div>
    </header>
  );
}

