  Crown,
  ArrowLeftRight,
  Building2,
  Wheat,
  ChevronDown,
} from "lucide-react";
import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

// POLOE Platform Apps
const CURRENT_APP = "VES";

const POLOE_APPS = [
  { name: "BOOK", href: "https://poloe.net", icon: BookOpen },
  { name: "KEYS", href: "https://keys.poloe.net", icon: Crown },
  { name: "VES", href: "https://ves.poloe.net", icon: ArrowLeftRight },
  { name: "DOTBOC", href: "https://dotboc.poloe.net", icon: Building2 },
  { name: "DEGANIA", href: "https://degania.poloe.net", icon: Wheat },
];

const navigation = [
  { name: "Dashboard", href: "/dashboard", icon: LayoutDashboard },
  { name: "Acquisitions", href: "/acquisitions", icon: Target },
  { name: "Members", href: "/members", icon: Users },
  { name: "Invites", href: "/invites", icon: Mail },
];

export function Sidebar() {
  const pathname = usePathname();
  const user = useQuery(api.users.me);

  // Get current app info
  const currentApp = POLOE_APPS.find((app) => app.name === CURRENT_APP) || POLOE_APPS[2];
  const CurrentIcon = currentApp.icon;

  return (
    <div className="flex h-full w-64 flex-col border-r border-border bg-card">
      {/* Logo / App Switcher */}
      <div className="flex h-16 items-center gap-3 border-b border-border px-4">
        <span className="text-sm font-semibold tracking-wide text-foreground">
          POLOE
        </span>

        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" size="sm" className="gap-2 px-2">
              <CurrentIcon className="h-4 w-4" />
              <span className="font-medium">{currentApp.name}</span>
              <ChevronDown className="h-4 w-4 text-muted-foreground" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="start" className="w-48">
            {POLOE_APPS.map((app) => {
              const Icon = app.icon;
              const isCurrent = app.name === CURRENT_APP;
              return (
                <DropdownMenuItem key={app.name} asChild>
                  <Link
                    href={app.href}
                    className={`flex items-center gap-3 ${isCurrent ? "bg-muted" : ""}`}
                  >
                    <Icon className="h-4 w-4" />
                    <span>{app.name}</span>
                  </Link>
                </DropdownMenuItem>
              );
            })}
          </DropdownMenuContent>
        </DropdownMenu>
      </div>

      {/* Navigation */}
      <nav className="flex-1 space-y-1 px-3 py-4">
        {navigation.map((item) => {
          const isActive = pathname === item.href || pathname.startsWith(item.href + "/");
          return (
            <Link
              key={item.name}
              href={item.href}
              className={cn(
                "flex items-center gap-3 rounded-lg px-3 py-2 text-sm font-medium transition-colors",
                isActive
                  ? "bg-accent text-accent-foreground"
                  : "text-muted-foreground hover:bg-accent/50 hover:text-accent-foreground"
              )}
            >
              <item.icon className="h-5 w-5" />
              {item.name}
            </Link>
          );
        })}
      </nav>

      {/* User info */}
      {user && (
        <div className="border-t border-border p-4">
          <div className="flex items-center gap-3">
            <div className="h-8 w-8 rounded-full bg-muted flex items-center justify-center text-sm font-medium text-foreground">
              {user.name?.charAt(0).toUpperCase() ?? "?"}
            </div>
            <div className="flex-1 min-w-0">
              <p className="truncate text-sm font-medium text-foreground">
                {user.name}
              </p>
              <p className="truncate text-xs text-muted-foreground">
                {user.role === "admin" ? "Admin" : "Member"}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

